CREATE TABLE Cliente (id INTEGER NOT NULL GENERATED ALWAYS AS IDENTITY (START WITH 1, INCREMENT BY 1), nome VARCHAR(100) NOT NULL, sexo VARCHAR(1) NOT NULL, dataNascimento DATE NOT NULL, rg VARCHAR(12) NOT NULL, emissor VARCHAR(6) NOT NULL, cpf VARCHAR(14) NOT NULL, cep VARCHAR(9) NOT NULL, logradouro VARCHAR(100) NOT NULL, complemento VARCHAR(100), cidade VARCHAR(100) NOT NULL, bairro VARCHAR(100) NOT NULL, estado VARCHAR(2) NOT NULL, PRIMARY KEY (id));